string datetimeStr = dateString+" "+timeString;
string datetimeFmt = "yyyy-MM-dd HH:mm:ss";
if (!msec.IsEmpty)
{
  datetimeStr += msecString;
  datetimeFmt += ":fff";
}
if (!zone.IsEmpty)
{
  datetimeStr += zoneString;
  datetimeFmt += "zzz";
}
return TO_DATETIME(datetimeStr, datetimeFmt);

switch (sevString)
{
case "WARNING":
	return Severity.Warning;
case "FATAL":
	return Severity.Error;
}
return Severity.Info;
